import 'package:flutter/material.dart';
import 'package:inc/msgescreen.dart';

class Messege  extends StatelessWidget {
  final String title;
  const Messege ({super.key,required this.title});

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      body:  
      ListView.separated(itemBuilder: (context,index){
        return ListTile(
          leading: CircleAvatar(backgroundImage: NetworkImage("data:image/jpeg;base64,"),),
          title: Text("person $index"),
          subtitle: Text(title),
          trailing: Icon(Icons.notifications_active,color: Colors.black,),

          onTap: (){
            Navigator.of(context).push(MaterialPageRoute(builder: (context){
              return Screentwoo(name: "person $index");
            }
            )
            );
          },
        );
      },
       separatorBuilder: (context,index){
        return Divider();
       },
        itemCount: 10
        ),
      
    );
  }
}